
-- Create NAICS Codes Table
CREATE TABLE naics_codes (
    naics_code INT PRIMARY KEY,
    description TEXT NOT NULL
);

-- Create Technologies Table
CREATE TABLE technologies (
    id SERIAL PRIMARY KEY,
    category TEXT NOT NULL,
    subcategory TEXT NOT NULL,
    naics_code INT REFERENCES naics_codes(naics_code),
    supply_chain_position TEXT,
    trl INT,
    potential_applications TEXT
);

-- Create Companies Table (Placeholder)
CREATE TABLE companies (
    id SERIAL PRIMARY KEY,
    name TEXT NOT NULL,
    naics_code INT REFERENCES naics_codes(naics_code)
);

-- Create Technology-Companies Relationship Table
CREATE TABLE technology_companies (
    technology_id INT REFERENCES technologies(id),
    company_id INT REFERENCES companies(id),
    PRIMARY KEY (technology_id, company_id)
);

